mapper.select:
/* 내 파일 목록 조회 */
SELECT	file_id
		,member_id
		,file_name
		,file_url
		,regi_date
FROM	myfile_info
WHERE	member_id = #{member_id}
ORDER BY file_id;

mapper.insert:
/* 내 파일 저장 */
INSERT	INTO	myfile_info (
	file_id
	,member_id
	,file_name
	,file_url
	,regi_date
)
VALUES	(
	SEQ_FILE_ID.nextval
	,#{member_id}
	,#{file_name}
	,#{file_url}
	,formatdatetime(now(),'yyyy-MM-dd/HH:mm:ss')
)

mapper.update:
/* 내 파일 정보 수정 */
UPDATE	myfile_info
SET 	file_name = #{file_name}
WHERE	member_id = #{member_id}
AND		file_id = #{file_id}
