mapper.select:
/* 내 파일 목록 조회 */ SELECT AA.id ,AA.member_id ,AA.file_type ,AA.file_depth ,AA.file_parent ,AA.file_id ,AA.file_nicname FROM myfile_info as AA WHERE AA.use_yn = 'Y'
AND AA.FILE_TYPE = 'folder'
ORDER BY AA.file_parent, AA.id;

mapper.insert:
/* 내 파일 저장 */
INSERT	INTO	myfile_info (
	member_id
	,file_type
	,file_depth
	,file_parent
	,file_id
	,file_nicname
	,use_yn
)
VALUES	(
	#{member_id}
	,#{file_type}
	,#{file_depth}
	,#{file_parent}
	,#{file_id}
	,#{file_nicname}
	,'Y'
)

/* 파일 저장 */
INSERT	INTO	file_info (
	file_name
	,file_path
)
VALUES	(
	#{file_name}
	,#{file_path}
)

mapper.update:
/* 내 파일 정보 수정 */
UPDATE	myfile_info
SET 	file_depth = #{file_depth}
		,file_parent = #{file_parent}
		,file_nicname = #{file_nicname}

		,use_yn = #{use_yn}

WHERE	member_id = #{member_id}
AND		file_id = #{file_id}
